<script>
  document.addEventListener("DOMContentLoaded", function() {
    var line = document.querySelector("#line");
    var navbar = document.querySelector("#navbarKedua");

    window.addEventListener("scroll", function() {
      var scrolled = window.scrollY;
      var navbarHeight = navbar.offsetHeight;
      var viewportHeight = window.innerHeight;

      var maxScroll = document.body.clientHeight - viewportHeight;

      var scrolledPercentage = (scrolled / maxScroll) * 100;

      if (scrolledPercentage > 100) {
        line.style.width = "100%";
        line.style.backgroundColor = "#FD9B00";
      } else {
        line.style.width = scrolledPercentage + "%";
      }
    });
  });
</script>
